// utils/successResponseApi.js

export const successResponseApi = (res, statusCode = 200, message = "Success", data = {}) => {
  return res.status(statusCode).json({
    success: true,
    message,
    data,
  });
};


// utils/errorResponseApi.js

export const errorResponseApi = (
  res,
  statusCode = 500,
  error = "Something went wrong",
) => {
  return res.status(statusCode).json({
    success: false,
    error,
  });
};
